const addSkill = () => {
  const addSkillBtn = document.getElementById('add-skill-btn');
  const fieldsDiv = document.querySelector('.teacher_fields_name');
  const levelsDiv = document.querySelector('.teacher_levels_name');

  if (addSkillBtn) {
    let counter = 1;

    // const createOption = (name, divToAppend) => {
    //   const option = document.createElement("option")
    //   option.text = `${name}`
    //   divToAppend.add(option)
    // };

    addSkillBtn.addEventListener('click', (e) => {
      e.preventDefault();
      const select = document.createElement("SELECT");
      // createOption('mathematics', select);
      // createOption('english', select);
      // createOption('history', select);
      // createOption('biology', select);
      // createOption('physics', select);

      select.setAttribute('class', "form-control select optional");
      select.setAttribute('name', `teacher[fields_attributes][${counter}][name]`);
      select.setAttribute('id', `teacher_fields_attributes_${counter}_name`);

      fieldsDiv.insertAdjacentHTML('beforeend', select);

      counter++;
    });
  }
};

export { addSkill };
